import React from "react";
import styled from "styled-components";
import { useState } from "react";
import Eyes from "../assets/img/carbon_view.png"
import { BASE_URL } from "../assets/API/axios";
import axios from "axios";

const Login = () => {
    const [id, setId] = useState();
    const [password, setPassword] = useState();
    const [passwordType, setPasswordType] = useState({
        type: 'password',
        visible: false
    });

    const ChangePasswordType = ( e ) => {
        setPasswordType(() => {
            if (!passwordType.visible) {
                return { type: 'text', visible: true };
            }
            return { type: 'password', visible: false };
        })
    }

    return(
        <>
        <Center>
            <Wrapper>
                <SignUpText>회원가입</SignUpText>
                <div>
                    <Text>아이디</Text>
                    <TextInput
                    placeholder="6 ~ 20글자 이하여야 합니다."
                    type="text"
                    onChange={(e) => {
                        setId(e.target.value);
                    }}
                    />
                </div>
                <div>
                    <Text>비밀번호</Text>
                    <TextInput
                    placeholder="대문자, 소문자, 숫자, 특수문자가 최소 1개씩 포함되어야 합니다."
                    type={passwordType.type}
                    onChange={(e) => {
                        setPassword(e.target.value);
                    }}
                    />
                    <EyeImg onClick={ChangePasswordType} src={Eyes}></EyeImg>
                </div>
                <Btn
                onClick={Login}
                className="Btn"
                >완료</Btn>
            </Wrapper>
        </Center>
        </>
    )
}

export default Login

const EyeImg = styled.img`
    position: absolute;
    margin-left: -35px;
    margin-top: 14px;
`

const SignUpText = styled.div`
    font-weight: 700;
    font-size: 28px;
    margin-top: 40px;
    margin-bottom: 16px;
`

const Text = styled.div`
    font-weight: 700;
    font-size: 16px;
    margin-top: 10px;
    margin-bottom: 10px;
`

const TextInput = styled.input`
    width: 350px;
    border: 1px solid #999999;
    border-color: #999999;
    border-radius: 4px;
    border-width: 1.45px;
    padding: 16px 16px;
    padding-right: 40px;
`

const Center = styled.div`
    display: flex;
    justify-content: center;
    padding-top: 100px;
    min-height: 79.6vh;
    background-color: lightgray;
`

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    border: 0px solid gray;
    background-color: white;
    width: 490px;
    height: 580px;
`

const Btn = styled.button`
    margin-top: 30px;
    width: 410px;
    height: 48px;
    border-radius: 4px;
    border-width: 0px;
    font-weight: 700;
    font-size: 16px;
    color: white;
    background-color: #5F85BB;
`
