import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import EntryLogo from "../assets/img/EntryLogo.png"
import { useNavigate } from "react-router-dom";
import { useCookies } from 'react-cookie';

const Header = () => {
const [cookies, setCookie, removeCookie] = useCookies();

const Logout = () => {
    removeCookie("token");
    }

    return( 
    <>
        <Wrapper>
            <LogoWrapper>
                <LogoBack>
                    <LogoImg src={EntryLogo}></LogoImg>
                </LogoBack>
                <LogoText>Entry DSM</LogoText>
            </LogoWrapper>
        <TextWrapper>
        {!localStorage.getItem("token") &&
            (<>
                <LoginText>로그인</LoginText>
                <SignUpText>회원가입</SignUpText>
            </>
            )}
            {localStorage.getItem("token") && 
            (<>
            <TextDiv>글쓰기</TextDiv>
            <TextDiv>마이페이지</TextDiv>
            <TextDiv onClick={Logout}>로그아웃</TextDiv>
            </>
                )}
            </TextWrapper>
        </Wrapper>
        </>
        )
    }

export default Header

const LogoWrapper = styled.div`
    display: flex;
    align-items: center;
    margin-left: 361px;
`

const LogoBack = styled.div`
    border: 1px solid white;
    background-color: white;
    border-radius: 50%;
    width: 50px;
    height: 50px;
    position: relative;
`

const LogoImg = styled.img`
    position: absolute;
    right: 11px;
    top: 10px;
`

const LogoText = styled.div`
    color: white;
    font-weight: 700;
    font-size: 20px;
    margin-left: 10px;
`

const Wrapper = styled.div`
    width: 100vw;
    height: 80px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    border: 0px solid black;
    background-color: #5F85BB;
`

const TextWrapper = styled.div`
    display: flex;
    margin-right: 35px;
`

const TextDiv = styled.div`
    font-weight: 400;
    font-size: 20px;
    color: white;
`

const LoginText = styled.div`
    font-size: 20px;
    font-weight: 400;
    color: white;
    border-right: 1px solid white;
    padding-right: 10px;
`

const SignUpText = styled.div`
    font-size: 20px;
    font-weight: 400;
    color: white;
    padding-left: 10px;
`
