import React, { useEffect, useState } from 'react';
import nullPhoto from '../assets/img/Vector.png';
import axios from 'axios';
import '../App.css';
import styled from 'styled-components';
import { BASE_URL } from '../utils/API/axios';
import NotHover from '../assets/img/Group 24.png';
import Hover from '../assets/img/Group 25.png';

const MyIntroduce = () => {
	const [hoverCheck, setHoverCheck] = useState(NotHover);
	const [dataList, setDataList] = useState([]);
	const [TextStyle, setTextStyle] = useState();
	const [EditStyle, setEditStyle] = useState('None');
	const [introduceText, setIntoduceText] = useState();
	const [name, setName] = useState();
	const isDisabled = !(introduceText && name);

	useEffect(() => {
		axios
			.get(`${BASE_URL}/users/mypage`, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } })
			.then((Response) => {
				console.log(Response.data);
				setDataList(Response.data);
			})
			.catch((error) => {
				alert('실패');
			});
	}, []);

	return (
		<>
			<Wrapper>
				<PhotoBorder>
					<PhotoImg src={nullPhoto}></PhotoImg>
				</PhotoBorder>
				<div id={TextStyle}>
					<NameWrapper>
						<NameDiv>{dataList.name}</NameDiv>
						<AditImg
							onMouseLeave={() => {
								setHoverCheck(NotHover);
							}}
							onMouseEnter={() => {
								setHoverCheck(Hover);
							}}
							onClick={() => {
								setTextStyle('None');
								setEditStyle('');
							}}
							src={hoverCheck}
						></AditImg>
					</NameWrapper>
					<ContentDiv>{dataList.introduce}</ContentDiv>
				</div>
				<div id={EditStyle}>
					<InputWrapper>
						<Positioning>
							<NameInput placeholder="이름" />
							<SaveBtn
							disabled={isDisabled}
							>저장</SaveBtn>
						</Positioning>
						<IntroduceText placeholder="엔트리는 우리 학교의 최고의 동아리이며, 학생들의 입력부터 취업까지 도와주는 엄청난 동아리라고 할 수 있죠." />
					</InputWrapper>
				</div>
			</Wrapper>
		</>
	);
};

export default MyIntroduce;

const Positioning = styled.div`
	display: flex;
	justify-content: space-between;

	${Bt}:disabled {
		background-color: #e0e0e0;
	}
`

const SaveBtn = styled.button`
	width: 80px;
	height: 35px;
	background: #5f85bb;
	border-radius: 100px;
	border-width: 0px;
	color: white;
	font-weight: 700;
	font-size: 12px;
`

const InputWrapper = styled.div`
	display: flex;
	flex-direction: column;
	margin-left: 20px;
`;

const IntroduceText = styled.textarea`
	width: 600px;
	height: 39;
	padding: 10px 10px 10px 10px;
	border-width: 2px;
	resize: none;
	font-size: 12.5px;
	border: 1.5px solid #bebbbb;
	border-radius: 8px;
`;

const NameInput = styled.input`
	width: 80px;
	padding: 10px 10px 10px 7px;
	font-size: 15px;
	border: none;
	border-bottom: 1.5px solid #bebbbb;
	margin-bottom: 16px;
	margin-left: 3px;
	text-align: center;
`;

const NameWrapper = styled.div`
	display: flex;
`;

const AditImg = styled.img`
	width: 24px;
	height: 24px;
	margin-top: 8px;
	margin-left: 15px;
`;

const ContentDiv = styled.div`
	font-weight: 700;
	font-size: 16px;
	margin-top: 10px;
	margin-left: 37px;
	border: 0px solid black;
	width: 612px;
	word-break: break-all;
`;

const NameDiv = styled.div`
	font-weight: 700;
	font-size: 24px;
	margin-top: 5px;
	margin-left: 37px;
`;

const Wrapper = styled.div`
	display: flex;
	justify-content: center;
	margin-left: -10px;
	margin-top: 130px;
`;

const PhotoBorder = styled.div`
	border: 2px solid black;
	border-radius: 50%;
	width: 100px;
	height: 100px;
	display: flex;
	flex-direction: column;
	align-items: center;
`;

const PhotoImg = styled.img`
	padding-top: 20px;
	width: 80px;
`;
